import { Route, Routes, useLocation } from "react-router-dom";
import Header from "./components/header/header.jsx";
import Main from "./components/main/main.jsx";
import Registration from "./components/authorization/registration.jsx";
import './style/common.css';
//создание функции с названием App()
function App() { 
  const { pathname } = useLocation();

  let headerContainerClass  = '';

  if (pathname.startsWith('/registration')) {
    headerContainerClass  = '#f0f0f0'; // Любой цвет, который вам нужен
  }

  return (
    <>

  <Header containerClass={headerContainerClass} />
    <Routes>
      <Route path = "/user/:id" element={<Main/>} />
      <Route path = "/registration" element={<Registration/>} />
    </Routes>
    </>
  );
}

export default App;
