import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

function Main() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const params = useParams();
  const prodId = params.id;
  const refreshToken = sessionStorage.getItem("refreshToken");

  useEffect(() => {
    const url = `https://localhost:7073/api/Registration/GetAccessToken?minutes=300`;
    const headers = {
      Accept: "application/json, text/plain, */*",
      "Content-Type": "application/json;charset=utf-8",
      Authorization: `Bearer ${refreshToken}`
    };
  
    fetch(url, {
      method: "POST",
      headers: headers,
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        // Здесь вы можете обработать полученные данные, если это необходимо
        // Например, если в ответе содержится информация о пользователе, вы можете сохранить её в состояние items
       
      })
      .catch((error) => {
        console.error("Ошибка при загрузке данных:", error);
      });
  }, []); // Пустой массив зависимостей, чтобы useEffect выполнился только один раз после монтирования компонента

  let imgData = items.ImgName;
  if (imgData == null) {
    imgData = "cat.png";
  }

  return loading ? (
    <main>
      <div className="userCard">
        <div className="circle"></div>
        <img
          src={`https://localhost:7073/img/${imgData}`}
          alt={items.ImgName ? "Item Image" : "Default Image"}
        />
        <div className="rowInfo">
          <p>{items.name}</p>
          <p>{items.lastName}</p>
          <p>{items.patronymic}</p>
        </div>
      </div>
      <div className="circle"></div>
    </main>
  ) : (
    <p>Loading...</p>
  );
}

export default Main;
