import './style/header.css';
import {useLocation } from "react-router-dom";
import magnifier from './../../img/magnifier.png';
import userIcon from './../../img/Ellipse 6userIcon.png';
import basket from './../../img/Vectorbasket.svg';
import settings from './../../img/Vectorsettings.svg';
function Header() {
    let headerContainerClass  = '';
    const { pathname } = useLocation();  
    if (pathname.startsWith('/registration')) {
      headerContainerClass  = '#f0f0f0'; // Любой цвет, который вам нужен
    }
    return(
        <header>
            <div className='h-container'>
                <div className='header_row'>
                <div >
                    <img src={magnifier} className='search-img'></img>
                    <input className='search'></input>
                </div>
                <div className='menu'>
                    <img src={userIcon} className='icon-user' />
                    <img src={basket} className='icon-header'></img>
                    <img src={settings} className='icon-header'></img>
                </div>
                
                
                </div>
            </div>
        </header>
    )
}

export default Header;
